declare function _NS(localName: string): string;
/**
 * The Solid Service Description vocabulary describes the services provided by a Solid Server.
 */
declare const SOLID_SD: {
    PREFIX: string;
    NAMESPACE: string;
    PREFIX_AND_NAMESPACE: {
        solid_sd: string;
    };
    NS: typeof _NS;
    /**
     * A Solid Service is some function provided by a Solid Server,
   e.g., to search a Pod, send a notification to an Inbox, logout of a Pod, etc.
     *
     * This term provides descriptions only in English.
     */
    Service: string;
    /**
     * An instance of solid_sd:Feature represents a feature of a
   Solid service. Specific types of features might include protocols (or
   sub-protocols)
     *
     * The term has a description only in English, with [1] label in the language [NoLocale], but [1] comment in the language [en] (so the difference is only between English and NoLocale, which we consider the same).
     */
    Feature: string;
    /**
     * An instance of solid_sd:Protocol represents one specific
   protocol (or sub-protocol).
     *
     * The term has a description only in English, with [1] label in the language [NoLocale], but [1] comment in the language [en] (so the difference is only between English and NoLocale, which we consider the same).
     */
    Protocol: string;
    /**
     * A Solid Endpoint is the URL through which an agent requests
  that a Solid Service performs its function, e.g., the SPARQL endpoint for a
  "Search my Pod Solid Service.
     *
     * This term provides descriptions only in English.
     */
    endpoint: string;
    /**
     * Relates an instance of solid_sd:Service with a
   resource representing a supported feature.
     *
     * The term has a description only in English, with [1] label in the language [NoLocale], but [1] comment in the language [en] (so the difference is only between English and NoLocale, which we consider the same).
     */
    feature: string;
    /**
     * Relates an instance of solid_sd:Service to a protocol (or
   sub-protocol) that it implements (e.g., a WebSocket service might implement
   multiple, or just one, sub-protocols.
     *
     * This term provides descriptions only in English.
     */
    supportedProtocol: string;
    /**
     * A Solid Service providing SPARQL query capability - could be
   across data within a single Pod, or across all Pods hosted by a Solid Server
   provider.
     *
     * This term provides descriptions only in English.
     */
    serviceSparql: string;
    /**
     * A Solid Service providing GraphQL query capability. This
   service initially provides authenticated and authorized access to sensitive
   access control data associated with resources in Pods.
     *
     * This term provides descriptions only in English.
     */
    serviceGraphql: string;
    /**
     * A Solid Service providing the ability to 'power off' a Pod.
   This is useful in cases where a Pod owner wishes to completely disable all
   access to their entire Pod (e.g., in the case that they suspect access has been
   compromised).
     *
     * This term provides descriptions only in English.
     */
    servicePowerSwitch: string;
    /**
     * A Solid Service providing the ability to register a new
   a Pod. Generally weâ€™d expect this service to be provided by a Solid
   Identity Provider, although some server implementations may wish to offer this service directly themselves too.
     *
     * This term provides descriptions only in English.
     */
    servicePodRegistration: string;
    /**
     * A Solid Service providing the ability to subscribe to
   resource change events via a WebSocket connection.
     *
     * This term provides descriptions only in English.
     */
    serviceWebSocket: string;
    /**
     * solid-0.2
     *
     * This term has a label (in language [NoLocale]), but no long-form descriptions at all (i.e., the vocabulary doesn&#x27;t provide any &#x27;rdfs:comment&#x27; or &#x27;dcterms:description&#x27; meta-data).
     *
     * See also:
     *  - http://www.w3.org/ns/solid/service-description#WebSocketSubProtocol0_2
     */
    WebSocketSubProtocol0_2Label: string;
    /**
     * solid-0.1
     *
     * This term has a label (in language [NoLocale]), but no long-form descriptions at all (i.e., the vocabulary doesn&#x27;t provide any &#x27;rdfs:comment&#x27; or &#x27;dcterms:description&#x27; meta-data).
     *
     * See also:
     *  - http://www.w3.org/ns/solid/service-description#WebSocketSubProtocol0_1
     */
    WebSocketSubProtocol0_1Label: string;
};
export default SOLID_SD;
